import React,{useState,useEffect} from 'react'
import './main.css';
import axios from 'axios'
import { faEdit,faTrash,faCheck, faPaperPlane } from "@fortawesome/free-solid-svg-icons";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";

function CRUD(){

  const init={
      id:null,
      title:"",
  }

  const [Data,setData]=useState([])
  const [Item,setItem]=useState(init)
  const [CheckForUpdate,setCheckForUpdate]=useState(false)

  useEffect(() => {
      axios.get('http://127.0.0.1:8000/api/task-list/')
      .then(
          response=>{ 
            setData(response.data)     
          }
      )
      .catch(() => {
        console.log("error")
    })
  }, [Data])


  const handlechange=(e)=>{
    setItem({...Item,
          title:e.target.value,     
          })  
  }


  const handleSubmit = async () => {
    try {
        const resp = await axios.post('http://127.0.0.1:8000/api/task-create/',Item)
       // setItem(Item)
        console.log(resp.data)
    } 
    catch (err) {
        // Handle Error Here
        console.error(err)
    }
}

  const Edit=(Item)=>{
        setCheckForUpdate(true)
        setItem(Item)   
  }

  const handleEdit=()=>{

      axios.post(`http://127.0.0.1:8000/api/task-update/${Item.id}/`,
      {
          title:Item.title,     
      });
      setCheckForUpdate(false)
      setItem(Item)
  }

const handleDelete = async Item => {

    try {
        const resp = await axios.delete(`http://127.0.0.1:8000/api/task-delete/${Item.id}/`)
        console.log(resp.data);
    }   
    catch (err) {
        console.log("error")
    }
}

return(

    <div className="containerx">
    <div id="task-containerx">
    <div  id="form-wrapperx">
    <form className="form" onSubmit={e=>(CheckForUpdate)?handleEdit(e):handleSubmit(e) }>
                  <div className="flex-wrapperx">
                      <div style={{flex: 6}}>
                      <input type="text" className="form-control mr-1" onChange={e=>handlechange(e)} value={Item.title} id="titleInput" placeholder="add item "/>
                     </div>
                      <div style={{flex: 1}}>          
               <button id="submit" className="button" type="submit" value="Add">
               <FontAwesomeIcon icon={faPaperPlane} /></button>

              </div>                
                  </div>
                  </form>    
              </div>

              <div  id="list-wrapperx"> 
              {Data.map((Item,index)=>( 
                  <div key={index} className="task-wrapperx flex-wrapperx">

                  <div style={{flex:7}}>
                  {Item.title}
                          </div>

                          <div style={{flex:1}}>
                              <button onClick={() => Edit(Item)}  className="button buttonex buttone"><FontAwesomeIcon icon={faEdit} /></button>
                          </div>

                          <div style={{flex:1}}>
                              <button onClick={() => handleDelete(Item)} className="button buttonex buttonr"><FontAwesomeIcon icon={faTrash} /></button>
                          </div>

                          </div>
                      ))}
        </div>
        </div>
      </div>

)
}
export default CRUD
