import React,{useState,useEffect} from 'react'
import { API } from './API'
import { API_Post } from './API_Post'
import './App.css';
import axios from 'axios'
import { faEdit,faTrash,faCheck, faPaperPlane } from "@fortawesome/free-solid-svg-icons";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";

function App(){

  const init={
      id:null,
      title:"",
  }

  const [Data,setData]=useState([])
  const [Item,setItem]=useState(init)
  const [CheckForUpdate,setCheckForUpdate]=useState(false)
  const [Request,setRequest]=useState(false)

  useEffect(() => {
      axios.get('http://127.0.0.1:8000/api/task-list/')
      .then(
          response=>{ 
            setData(response.data)     
          }
      )
      .catch(() => {
        console.log("error")
    })
  }, [Request])


  const handlechange=(e)=>{
    setItem({...Item,
          title:e.target.value,     
          })

  }

  const handleSubmit=()=>{
  axios.post('http://127.0.0.1:8000/api/task-create/',Item)
  setItem(init)
  setRequest(Item)
  }

  const Edit=(Item)=>{
          setCheckForUpdate(true)
          setItem(Item)   
  }

  const handleEdit=()=>{

      axios.post(`http://127.0.0.1:8000/api/task-update/${Item.id}/`,
      {
          title:Item.title,

      });
      setCheckForUpdate(false)
      setItem(init)
      setRequest(Item)

  }

const handleDelete=(Item)=>{
    axios.delete(`http://127.0.0.1:8000/api/task-delete/${Item.id}/`)
    .then((response) =>{
      setRequest(Item)
    })
  }

return(

    <div className="container">
    <div id="task-container">
    <div  id="form-wrapper">
    <form className="form" onSubmit={e=>(CheckForUpdate)?handleEdit(e):handleSubmit(e) }>
                  <div className="flex-wrapper">
                      <div style={{flex: 6}}>
                      <input type="text" className="form-control mr-1" onChange={e=>handlechange(e)} value={Item.title} id="titleInput" placeholder="add task..."/>
                      <div style={{flex: 1}}>  
                      </div>
              </div>                
                  </div>
                  </form>    
              </div>

              <div  id="list-wrapper"> 
              {Data.map((Item,index)=>( 
                  <div key={index} className="task-wrapper flex-wrapper">

                  <div style={{flex:7}}>
                  {Item.title}
                          </div>

                          <div style={{flex:1}}>
                              <button onClick={() => Edit(Item)}  className="btn buttonex buttone"><FontAwesomeIcon icon={faEdit} /></button>
                          </div>



                          <div style={{flex:1}}>
                              <button onClick={() => handleDelete(Item)} className="btn buttonex buttonr"><FontAwesomeIcon icon={faTrash} /></button>
                          </div>

                          </div>
                      ))}
        </div>
        </div>
      </div>

)
}
export default App
