const initialTask={
    id:null,
    title:"",
    is_done: false
}
const [tasks,setTasks]=useState([])
const [task,setTask]=useState(initialTask)
const [updateOrCreate,setUpdateOrCreate]=useState(false)
const [fetch,setFetch]=useState(false)
const done =(task)=>{
    return (task.is_done)?"fa fa-check-circle fa-2x text-success":"fa fa-check-circle fa-2x"
}

useEffect(() => {
    axios.get('http://127.0.0.1:8000/api/task-list/')
    .then(
        response=>{

            setTasks(response.data)
            console.log('fetched data ...')
        }
    )
    .catch(
        console.log("error")

    )

},[fetch])


const handlechange=(e)=>{
   setTask({...task,
           title:e.target.value,

        })
    console.log('handle change:'+ task)
}

const handleSubmit=(e)=>{
 e.preventDefault()
 axios.post('http://127.0.0.1:8000/api/task-create/',task, {
    method:'DELETE',
    headers:{
      'Content-type':'application/json',

    }})
setTask(initialTask)
setFetch(!fetch)

}

const handleToUpdate=(task)=>{
    setTask(task)
    setUpdateOrCreate(true)
}

const handleUpdate=(e)=>{
    e.preventDefault()
     console.log(task)
     axios.post('http://127.0.0.1:8000/api/task-update/'+task.id,
     {
        title:task.title,
        is_done:task.is_done
     });
     setUpdateOrCreate(false)
     setTask(initialTask)
     setFetch(!fetch)

}
const handleDelete=(task)=>{

    axios.delete(`http://127.0.0.1:8000/api/task-delete/${task.id}/`, {
      method:'DELETE',
      headers:{
        'Content-type':'application/json',

      },
    }).then((response) =>{

        setFetch(task)
    })
  }


  const handleComplete=(task)=>{
    task.completed = !task.completed
    axios.post('http://127.0.0.1:8000/api/task-update/'+task.id,
    {
        method:'POST',
        headers:{
          'Content-type':'application/json',

        },

        body:JSON.stringify({'completed': task.completed, 'title':task.title})
      }) 

    setFetch(task)
}

