    return(

        <div className="list-container col-md-6">
        <div className="form-container">
           <form className="form-group" onSubmit={e=>(updateOrCreate)?handleUpdate(e):handleSubmit(e) }>
             <input type="text" className="form-control mr-1" onChange={e=>handlechange(e)} value={task.title} id="titleInput" placeholder="add task..."/>
             <button className="btn btn-success">submit</button>
          </form>
        </div>


        <div className="tasks-container">


        {tasks.map((task,index)=>( 
            <div className="task" key={index}>
                <div style={{flex:20}}>{task.title}</div>

               <div className="d-flex" style={{flex:3}}>
                  <button className="btn btn-default mr-1" onClick={()=>handleComplete(task)}><FontAwesomeIcon icon={faCheck} /></button>
        <button  className="btn btn-default mr-1" onClick={()=>handleToUpdate(task)}  ><FontAwesomeIcon icon={faEdit} /></button>
                   <button className="btn btn-default" onClick={()=>handleDelete(task)}><FontAwesomeIcon icon={faTrash} /></button>
              </div>

            </div>
        ))}

        </div>




      </div>
       );
    }

export default Todo
