import React,{useState,useEffect} from 'react'
import axios from 'axios'
import Delete from './Delete'
import Update from './Update'
import { faEdit,faTrash,faCheck, faPaperPlane } from "@fortawesome/free-solid-svg-icons";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";

function Create(){

    const init={
        id:null,
        title:"",
    }

    const [Data,setData]=useState([])
    const [Item,setItem]=useState(init)
    const [CheckForUpdate,setCheckForUpdate]=useState(false)

    const handlechange=(e)=>{
        setItem({...Item,
              title:e.target.value,     
              })  
      }

const Edit=(Item)=>{
    setCheckForUpdate(true)
    setItem(Item)   
}

const handleEdit=()=>{

axios.post(`http://127.0.0.1:8000/api/task-update/${Item.id}/`,
{
    title:Item.title,     
});
setCheckForUpdate(false)
setItem(Item)
}

    const handleSubmit=()=>{
        axios.post('http://127.0.0.1:8000/api/task-create/',Item)
        setItem(Item)

      }
      return(
        <form className="form" onSubmit={e=>(CheckForUpdate)?handleEdit(e):handleSubmit(e) }>
        <div className="flex-wrapper">
            <div style={{flex: 6}}>
            <input type="text" className="form-control mr-1" onChange={e=>handlechange(e)} value={Item.title} id="titleInput" placeholder="add item "/>
           </div>
            <div style={{flex: 1}}>          
     <button id="submit" className="btn btn-warning" type="submit" value="Add">
     <FontAwesomeIcon icon={faPaperPlane} /></button>

    </div>                
        </div>
        </form> 
    )
}

export default Create
