import React,{useState,useEffect} from 'react'
import axios from 'axios'
import Create from './Create'
import Delete from './Delete'
import Update from './Update'
import { faEdit,faTrash,faCheck, faPaperPlane } from "@fortawesome/free-solid-svg-icons";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";

function Read(props){

    const init={
        id:null,
        title:"",
    }

    const [Data,setData]=useState([])
    const [Item,setItem]=useState(init)
    const [CheckForUpdate,setCheckForUpdate]=useState(false)


useEffect(() => {
    axios.get('http://127.0.0.1:8000/api/task-list/')
    .then(
        response=>{ 
          setData(response.data)     
        }
    )
    .catch(() => {
      console.log("error")
  })
}, [Data])



return(

    <div className="container">
    <div id="task-container">
    <div  id="form-wrapper">
    <form className="form" onSubmit={e=>(CheckForUpdate)?handleEdit(e):handleSubmit(e) }>
                  <div className="flex-wrapper">
                      <div style={{flex: 6}}>
                      <input type="text" className="form-control mr-1" onChange={e=>handlechange(e)} value={Item.title} id="titleInput" placeholder="add item "/>
                     </div>
                      <div style={{flex: 1}}>          
               <button id="submit" className="btn btn-warning" type="submit" value="Add">
               <FontAwesomeIcon icon={faPaperPlane} /></button>

              </div>                
                  </div>
                  </form>    
              </div>

              <div  id="list-wrapper"> 
              {Data.map((Item,index)=>( 
                  <div key={index} className="task-wrapper flex-wrapper">

                  <div style={{flex:7}}>
                  {Item.title}
                          </div>

                          <div style={{flex:1}}>
                              <button onClick={() => props.Edit(Item)}  className="btn buttonex buttone"><FontAwesomeIcon icon={faEdit} /></button>
                          </div>

                          <div style={{flex:1}}>
                              <button onClick={() => handleDelete(Item)} className="btn buttonex buttonr"><FontAwesomeIcon icon={faTrash} /></button>
                          </div>

                          </div>
                      ))}
        </div>
        </div>
      </div>

)
}

export default Read
