import React,{useState,useEffect} from 'react'
import './main.css';
import axios from 'axios'
import { faEdit,faTrash,faCheck, faPaperPlane } from "@fortawesome/free-solid-svg-icons";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";

function CRUD(){

  const [data,setData]=useState([])
  const [fetch,setFetch]=useState(false)
  const [item,setItem]=useState({id:null,title:""})
  const [checkForUpdate,setCheckForUpdate]=useState(false)

  useEffect(() => {
    const fetchData = async () =>{
      try {
        const {data: response} = await axios.get('http://127.0.0.1:8000/api/crud_get_post')
        setData(response);
      } catch (error) {
        console.error("error");
      }
    }
    fetchData();
  }, [fetch]);


  const handlechange=(event)=>{
    setItem({...item, title:event.target.value, })
    }

   const handleSubmit = async () => {
        try {
            const resp = await axios.post('http://127.0.0.1:8000/api/crud_get_post/',item)
            setFetch(fetch)
            console.log(resp.data)
        } 
        catch (err) {
            console.log("error")
        }
    }  

    const Edit=(item)=>{
        setCheckForUpdate(true)
        setFetch(fetch)
        setItem(item)   
    }  

    const handleEdit = async () => {
        try {
            const resp = await axios.put(`http://127.0.0.1:8000/api/crud_update_delete/${item.id}/`,
            {
                title:item.title,     
            })
            setFetch(fetch)
            setCheckForUpdate(false)
            console.log(resp.data)
        } catch (err) {
            console.error(err)
        }
    }

    const handleDelete = async item => {

        try {
            const resp = await axios.delete(`http://127.0.0.1:8000/api/crud_update_delete/${item.id}/`)
            setFetch(!fetch)  
            console.log(resp.data);

        }   
        catch (err) {
            console.log("error")
        }
    }


    return(

    <div className="container">
        <div id="task-containerx">
            <div  id="form-wrapperx">
                <form className="form" onSubmit={event=>(checkForUpdate)?handleEdit(event):handleSubmit(event) }>
                    <div className="flex-wrapperx">
                            <div style={{flex: 6}}>
                                <input type="text" className="form-control mr-1" onChange={event=>handlechange(event)} value={item.title} id="titleInput" placeholder="add item "/>
                            </div>
                            <div style={{flex: 1}}>          
                                <button id="submit" className="button" type="submit" value="Add">
                                <FontAwesomeIcon icon={faPaperPlane} /></button>       
                            </div>                
                        </div>
                </form>   
            </div>

            <div  id="list-wrapperx"> 
                {data.map((item,index)=>( 
                <div key={index} className="task-wrapperx flex-wrapperx">        
                    <div style={{flex:7}}>
                        {item.title}
                    </div>

                    <div style={{flex:1}}>
                        <button onClick={() => Edit(item)}  className="button buttonex buttone"><FontAwesomeIcon icon={faEdit} /></button>
                    </div>

                    <div style={{flex:1}}>
                        <button onClick={() => handleDelete(item)} className="button buttonex buttonr"><FontAwesomeIcon icon={faTrash} /></button>
                    </div>

                </div>
                ))}

            </div>
        </div>
    </div>

    )
}
export default CRUD
