import React from 'react';
import './App.css';
import { faEdit,faTrash,faCheck, faPaperPlane } from "@fortawesome/free-solid-svg-icons";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import axios from 'axios'

class App extends React.Component {
  constructor(props){
    super(props);
      this.state = {
        todoList:[],
        activeItem:{
          id:null, 
          title:'',
          completed:false,
        },
        editing:false,
      }
      this.fetchTasks = this.fetchTasks.bind(this)
      this.handleChange = this.handleChange.bind(this)
      this.handleSubmit = this.handleSubmit.bind(this)


      this.startEdit = this.startEdit.bind(this)
      this.deleteItem = this.deleteItem.bind(this)
      this.strikeUnstrike = this.strikeUnstrike.bind(this)
  };


  componentWillMount(){
    this.fetchTasks()
  }

  fetchTasks(){
    console.log('Fetching...')

    fetch('http://127.0.0.1:8000/api/task-list/')
    .then(response => response.json())
    .then(data => 
      this.setState({
        todoList:data
      })
      )
  }

  handleChange(e){
    var name = e.target.name
    var value = e.target.value
    console.log('Name:', name)
    console.log('Value:', value)

    this.setState({
      activeItem:{
        ...this.state.activeItem,
        title:value
      }
    })
  }

  handlebutton(){

    alert(" Publishing is disabled on this Demo to test this feature please download the source code and test it on a local machine")
  }
  handleSubmit(e){
    e.preventDefault()
    console.log('ITEM:', this.state.activeItem)



    var url = 'http://127.0.0.1:8000/api/task-create/'

    if(this.state.editing == true){
      url = `http://127.0.0.1:8000/api/task-update/${ this.state.activeItem.id}/`
      this.setState({
        editing:false
      })
    }



    fetch(url, {
      method:'POST',
      headers:{
        'Content-type':'application/json',
      },
      body:JSON.stringify(this.state.activeItem)
    }).then((response)  => {
        this.fetchTasks()
        this.setState({
           activeItem:{
          id:null, 
          title:'',
          completed:false,
        }
        })
    }).catch(function(error){
      console.log('ERROR:', error)
    })

  }

  startEdit(task){
    this.setState({
      activeItem:task,
      editing:true,
    })
  }


  deleteItem(task){


    fetch(`http://127.0.0.1:8000/api/task-delete/${task.id}/`, {
      method:'DELETE',
      headers:{
        'Content-type':'application/json',

      },
    }).then((response) =>{

      this.fetchTasks()
    })
  }

  handleDelete=(task)=>{

    axios.delete(`http://127.0.0.1:8000/api/task-delete/${task.id}/`, {
      method:'DELETE',
      headers:{
        'Content-type':'application/json',

      },
    }).then((response) =>{

      this.fetchTasks()
    })
  }

  strikeUnstrike(task){

    task.completed = !task.completed

    var url = `http://127.0.0.1:8000/api/task-update/${task.id}/`

      fetch(url, {
        method:'POST',
        headers:{
          'Content-type':'application/json',

        },
        body:JSON.stringify({'completed': task.completed, 'title':task.title})
      }).then(() => {
        this.fetchTasks()
      })

    console.log('TASK:', task.completed)
  }


  render(){
    var tasks = this.state.todoList
    var self = this
    return(
        <div className="container">

          <div id="task-container">
              <div  id="form-wrapper">
                 <form onSubmit={this.handleSubmit}  id="form">
                    <div className="flex-wrapper">
                        <div style={{flex: 6}}>
                            <input onChange={this.handleChange} className="form-control" id="title" value={this.state.activeItem.title} type="text" name="title" placeholder="Add task.." />
                         </div>

                         <div style={{flex: 1}}>


                            <button id="submit" className="btn btn-warning" type="submit" value="Add">
                            <FontAwesomeIcon icon={faPaperPlane} /></button>
                          </div>
                      </div>
                </form>

              </div>

              <div  id="list-wrapper">         
                    {tasks.map(function(task, index){
                      return(
                          <div key={index} className="task-wrapper flex-wrapper">

                            <div style={{flex:7}}>


                                {task.completed == false ? (
                                    <span className='not_completed'>{task.title}</span>

                                  ) : (

                                    <div classname="xcompleted"><div className='completed-text'>{task.title}</div><span className='completed_checkbox'>âœ“</span></div>
                                  )}

                            </div>


                            <div style={{flex:1}}>
                            <button onClick={() => self.strikeUnstrike(task)} className="btn buttonex buttonp"><FontAwesomeIcon icon={faCheck} /></button>

                            </div>



                            <div style={{flex:1}}>
                                <button onClick={() => self.startEdit(task)}  className="btn buttonex buttone"><FontAwesomeIcon icon={faEdit} /></button>
                            </div>



                            <div style={{flex:1}}>
                                <button onClick={() => self.handleDelete(task)} className="btn buttonex buttonr"><FontAwesomeIcon icon={faTrash} /></button>
                            </div>

                          </div>
                        )
                    })}
              </div>
          </div>

        </div>
      )
  }
}



export default App;
