import React from 'react';
import './App.css';
import { faEdit,faTrash,faCheck, faPaperPlane,faRefresh, faArrowCircleUp, faPeace, faPen, faPenSquare, faPaintBrush, faPaintRoller, faMarker } from "@fortawesome/free-solid-svg-icons";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import axios from 'axios'

function App() {
	const [todos, setTodos] = useState([])

	const getTodos = async () => {
		try {
			const response = await axios.get('/api/v1/todo/')
			const { data } = response
			setTodos(data)
		} catch (err) {
			console.log(err)
		}
	}

	useEffect(() => {
		getTodos()
	}, [])

	const addTodo = async newTodo => {
		try {
			console.log(newTodo)
			await axios.post('/api/v1/todo/', newTodo)
			getTodos()
		} catch (err) {
			console.log(err)
		}
	}
	const editTodo = async todo => {
		try {
			await axios.put(`/api/v1/todo/${todo.id}/`, todo)
			getTodos()
		} catch(err) {
			console.log(err)
		}
	}

	const deleteTodo = async id => {
		try {
			await axios.delete(`/api/v1/todo/${id}/`)
			getTodos()
		} catch(err) {
			console.log(err)
		}
	}
