import React,{useState,useEffect} from 'react'
import { API } from './API'
import { API_Post } from './API_Post'
import './App.css';
import axios from 'axios'
import { faEdit,faTrash,faCheck, faPaperPlane } from "@fortawesome/free-solid-svg-icons";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
function Todo(){
const initialTask={
    id:null,
    title:"",
    is_done: false
}
const [tasks,setTasks]=useState([])
const [task,setTask]=useState(initialTask)
const [updateOrCreate,setUpdateOrCreate]=useState(false)
const [fetch,setFetch]=useState(false)

useEffect(() => {
    axios.get('http://127.0.0.1:8000/api/task-list/')
    .then(
        response=>{ 
            setTasks(response.data)     
        }
    )
    .catch(() => {
      console.log("error")
  })
}, [fetch])




const handlechange=(e)=>{
   setTask({...task,
         title:e.target.value,     
        })

}

const handleSubmit=(e)=>{

 axios.post('http://127.0.0.1:8000/api/task-create/',task)
setTask(initialTask)
setFetch(!fetch)

}

const handleToUpdate=(task)=>{

        setUpdateOrCreate(true)
        setTask(task)

}

const handleUpdate=(e)=>{
    e.preventDefault()
    axios.post(`http://127.0.0.1:8000/api/task-update/${task.id}/`,
     {
        title:task.title,
        is_done:task.is_done
     });
     setUpdateOrCreate(false)
     setTask(initialTask)
     setFetch(!fetch)

}




const handleDelete=(task)=>{

    axios.delete(`http://127.0.0.1:8000/api/task-delete/${task.id}/`)
    .then((response) =>{

        setFetch(task)
    })
  }
    return(


        <div className="container">
        <div id="task-container">
        <div  id="form-wrapper">
        <form className="form" onSubmit={e=>(updateOrCreate)?handleUpdate(e):handleSubmit(e) }>

                     <div className="flex-wrapper">
                         <div style={{flex: 6}}>
                         <input type="text" className="form-control mr-1" onChange={e=>handlechange(e)} value={task.title} id="titleInput" placeholder="add task..."/>
                         <div style={{flex: 1}}>


                          </div>


                  </div>                
                     </div>
                     </form>    
                 </div>

                 <div  id="list-wrapper"> 
                 {tasks.map((task,index)=>( 
                      <div key={index} className="task-wrapper flex-wrapper">


                      <div style={{flex:7}}>
                      {task.title}

                             </div>







                             <div style={{flex:1}}>
                                 <button onClick={() => handleToUpdate(task)}  className="btn buttonex buttone"><FontAwesomeIcon icon={faEdit} /></button>
                             </div>



                             <div style={{flex:1}}>
                                 <button onClick={() => handleDelete(task)} className="btn buttonex buttonr"><FontAwesomeIcon icon={faTrash} /></button>
                             </div>

                             </div>
                          ))}
           </div>
           </div>
         </div>

 )
 }
export default Todo
